const n=async t=>{try{const e=await fetch("/generate-reset-link.php",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({email:t})}),r=await e.json();if(!e.ok||!r.success)throw new Error(r.error||"Error al generar enlace de restablecimiento");return r.resetLink}catch(e){throw console.error("Error generando enlace de reset:",e),e}};export{n as generatePasswordResetLink};
